import { useState, useEffect} from "react";
import { getSinglePosts } from "../contentful";

export default function useSinglePost (slug) {
    const [post, setPost]  = useState(null)
    const [isSingleLoading, setIsSingleLoading] = useState(true)

    useEffect(() => { 
        getSinglePosts(slug)
        .then( post => {
            setPost(post)
            setIsSingleLoading(false)
        })
        .catch( (e) => console.log(' Error in fetching this blog '))
    },[])

    return [ post, isSingleLoading]
}
