const client = require('contentful').createClient({
    space:'2ubxve0wmw2e',
    accessToken:'z4pul0Uh35pfNd9ph_FyG52HqiD61qnva3jWhvAo9kE'
});

const getPosts = () =>  client.getEntries().then(ent => ent.items);

const getSinglePosts = slug => client.getEntry({
    'fields.slug':slug,
    content_type: 'Blog-cms'
    })
    .then(post => post.items);


export  {getPosts, getSinglePosts}
