import React from "react";
import usePosts from "../custom-hooks/usePosts";

const blogHomeView = () => (
  <>
    <div className='BlogHome__main-post'>
        <div className='BlogHome__container'>
          <BlogPreview
            mode={1}
            post={{
              ...mainArticle,
              thumbnail: {
                ...mainArticle.thumbnail,
                'data-sizes': '100vw, (min-width: 768px) 60vw',
              },
            }}
          />
        </div>
      </div>

  </>
)

function GridViewBlogPost ({posts}) {
  console.log(posts)
  return (
    <div>
      {posts.map((post, index) => {
        const { title, slug, description, featureImage, dateTime, body  } = post.fields
        return (
          <>
            {index == 0 &&
             (
                <div className='BlogHome__main-post'>
                  <div className='BlogHome__container'>
                    <BlogPreview
                      mode={1}
                      post={{
                        ...mainArticle,
                        thumbnail: {
                          ...mainArticle.thumbnail,
                          'data-sizes': '100vw, (min-width: 768px) 60vw',
                        },
                      }}
                    />
                  </div>
                </div>
              )
            }
            { index > 0 && 
              (
                <div className='BlogHome__section-list' key={index}>
                  <div className='BlogHome__container'>
                    <span
                      data-cy='linkToSections'
                      className='BlogHome__full-post-link bold'
                      onClick={() => {
                        renderPostList(title);
                      }}
                    >
                      {title} <div className='BlogHome__caret' />
                    </span>
                    {renderList(newPostList, itemsGrid)}
                  </div>
                </div>
              )
            }


          </>
        );
      })}
    </div>
  )
}

export default function Posts() {
  const [posts, isLoading] = usePosts();

    return (
        <div className="posts__container">
          <h2>Articles</h2>
          {isLoading && <div> Fetching blogs from CMS </div>}
          {posts && <GridViewBlogPost posts={posts} /> }
        </div>
    )
}
